import * as React from "react";
import {HeaderButton, HeaderButtonProps} from "./HeaderButton";
import {makeStyles} from "@material-ui/core/styles";
import {Grid} from "@material-ui/core";
import FacebookIcon from '@material-ui/icons/Facebook';
import InstagramIcon from '@material-ui/icons/Instagram';

interface HeaderProps {
}

const useStyles = makeStyles((theme) => ({
    header: {
        height: '10vh',
        backgroundColor: "#000000",
        width: "100%"
    },
    mainButtons: {
        justify: "center"
    },
    homeButton: {
        fontFamily: "LuloCleanOneBold"
    },
    socialIcons: {
        justify: "flex-end"
    },
    cta: {
    }
}));

export const Header: React.FC<HeaderProps> =
    () => {
        const classes = useStyles();
        return (
            <React.Fragment>
                <Grid
                    item
                    xs={12}
                    container
                    justify="center"
                    alignItems="center"
                    className={classes.header}
                >
                    <Grid
                        item xs={8}
                        container
                        justify="center"
                        alignItems="center"
                        spacing={4}
                        style={{height:'100%'}}
                    >
                        <Grid item>
                            <HeaderButton ClassName={classes.homeButton} Text={"LUNFARDA TRAVEL"} Url={"/"}/>
                        </Grid>
                        {hButtons.map((x,i) =>
                            <Grid item key={i}>
                                <HeaderButton Text={x.Text} Url={x.Url} key={x.Text} subItems={x.subItems}/>
                            </Grid>
                        )}

                    </Grid>
                    {/*<Grid item xs={2} justify={"flex-end"} className={classes.cta}>*/}
                    {/*    <SocialIcons/>*/}
                    {/*</Grid>*/}
                </Grid>
            </React.Fragment>
        )
    }


const SocialIcons = () => {
    const classes = useStyles()
    return (
        <React.Fragment>
            <InstagramIcon style={{color:'white'}}/>
            <FacebookIcon style={{marginLeft:'0.8rem', color:'white'}}/>
        </React.Fragment>
    )
}

export default Header

/**
 * Urls and routing are handled in App.js
 */
export const hButtons : HeaderButtonProps[] = [
    {
        Text : "Buenos Aires",
        Url: "/buenos-aires",
        subItems: [
            {
                Text: "Neighborhoods",
                Url: "/neighborhoods"
            },
            {
                Text: "Culture",
                Url: "/culture"
            },
            {
                Text: "Day trips",
                Url:"/day-trips"
            }
        ]

    },
    {
        Text : "Kids",
        Url: "/kids"
    },
    {
        Text : "Jewish Heritage",
        Url: "/jewish-heritage"
    },
    {
        Text : "Argentina",
        Url: "/argentina"
    },
    {
        Text : "Book",
        Url: "/book"
    },
    {
        Text : "About Us",
        Url: "/about-us"
    },
    {
        Text : "FAQ",
        Url: "/faq"
    }
]
